import React from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Login from './components/Auth/Login.tsx';
import Home from './components/Home.tsx';

const App = () => {
  // Здесь можно добавить логику для проверки авторизации пользователя
  const isAuthenticated = false; // Пример: здесь должна быть логика проверки авторизации пользователя

  return (
    <Router>
      <Route path="/">
        {/* Показываем страницу входа, если пользователь не авторизован */}
        {!isAuthenticated ? <Login /> : <Home />}
      </Route>
      <Route path="/login">
        {/* Показываем страницу входа всегда, если пользователь не авторизован */}
        {!isAuthenticated && <Login />}
      </Route>
    </Router>
  );
};

export default App;
