import React, { Component } from 'react';
import { Route, Router, Routes } from 'react-router-dom';
import AppRoutes from './AppRoutes';
import { Layout } from './components/Layout';
import './custom.css';

export default function App() {
    return (
      <Router>
      <Switch>
        <Route path="/login">
          {/* Показываем страницу входа только если пользователь не авторизован */}
          {!isAuthenticated ? <LoginPage /> : <Redirect to="/" />}
        </Route>
        <Route path="/">
          {/* Показываем главную страницу только если пользователь авторизован */}
          {isAuthenticated ? <HomePage /> : <Redirect to="/login" />}
        </Route>
      </Switch>
    </Router>
      
    );
  }
