import React, { Component } from 'react';
import { Route, Router, Routes } from 'react-router-dom';
import AppRoutes from './AppRoutes';
import { Layout } from './components/Layout';
import Login from './components/Auth/Login.tsx';
import Home from './components/Home.tsx';
import './custom.css';

export default function App() {
  const isAuthenticated = false;

    return (
      <>
      <Layout>
        <Routes>
          {AppRoutes.map((route, index) => {
            const { element, ...rest } = route;
            return <Route key={index} {...rest} element={element} />;
          })}
        </Routes>
      </Layout>
      {!isAuthenticated && <Route path="/" component={Login} />}
      {/* Показываем главную страницу, если пользователь авторизован */}
      {isAuthenticated && <Route path="/" component={Home} />}
      {/* Показываем страницу входа всегда, если пользователь не авторизован */}
      {!isAuthenticated && <Route path="/login" component={Login} />}
      </>
      
      
    );
  }
