import React, { ChangeEvent, useState } from 'react'
import { Container } from 'reactstrap'
import Add from '../../public/Add.svg'
import styles from './admin.module.css'
import { Flex, Form, FormProps, Input } from 'antd'
import { LockOutlined, UserOutlined } from '@ant-design/icons';
import Button from '../Button/index.tsx'
import Multiselect from 'multiselect-react-dropdown';

interface Queue {
    name: string;
id: number;
}

function Admin() {
    const [user, setUser] = useState(false)
    const [login, setLogin] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRole] = useState('');
    const [cabinet, setCabinet] = useState('');
    const [window, setWindow] = useState('');
    const [queues, setQueues] = useState<string[]>([]);
    const [newQueue, setNewQueue] = useState('');
    const [formErrors, setFormErrors] = useState<{ [key: string]: string }>({});
    const [selectedQueue, setSelectedQueue] = useState<Queue | null>(null);
    const cities: Queue[] = [
        { name: 'Оформление личного дела', id: 1 },
        { name: 'Консультация по выбору специальностей', id: 2 },
        { name: 'Изменение приоритетов' , id: 3},
        { name: 'Прием оригиналов документов', id: 4},
    ];

    /*<select multiple value={queues} onChange={handleQueueChange}>
    <option value="filing"></option>
    <option value="consultation"></option>
    <option value="priority"></option>
    <option value="document"></option>
</select>*/

    const handleRoleChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
        setRole(event.target.value);
        if (event.target.value !== 'operator') {
            // Если роль не оператор, сбрасываем значения
            setQueues([]);
        }
    };

    const handleAddQueue = () => {
        setQueues([...queues, newQueue]);
        setNewQueue('');
    };

    const handleSubmit = (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();
        if (!login || !password || !role || (role === 'operator' && (!cabinet || !window))) {
            setFormErrors({ login: !login ? 'Логин обязателен' : '', password: !password ? 'Пароль обязателен' : '', role: !role ? 'Роль обязательна' : '', cabinet: role === 'operator' && !cabinet ? 'Кабинет обязателен' : '', window: role === 'operator' && !window ? 'Окно обязательно' : '' });
            return;
        }
        // Ваша логика отправки данных на сервер
        console.log({ login, password, role, cabinet, window, queues });
    };

    function handleAddUser(): void {
        setUser(true)
    }



    function handleNumericInputChange(value: string, setter: React.Dispatch<React.SetStateAction<string>>): void {
        const newValue = value.replace(/\D/g, ''); // Удаляем все, кроме цифр
        setter(newValue);
    }

    return (
        <Container className='d-flex flex-column align-items-center justify-content-center'>
            <h1>Панель управления</h1>
            <Container>
                <h3>Управление кабинетами</h3>
                <img className={styles.image} src={Add} onClick={handleAddUser}></img>
                {user && <form className={styles.addUserForm} onSubmit={handleSubmit}>
                    <Flex>
                        <div className={styles.formGroup}>
                            <label>Логин:</label>
                            <input type="text" value={login} onChange={(e) => setLogin(e.target.value)} />
                        </div>
                        <div className={styles.formGroup}>
                            <label>Пароль:</label>
                            <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
                        </div>
                        <div className={styles.formGroup}>
                            <label>Роль:</label>
                            <select title='Выберите роль' value={role} onChange={handleRoleChange}>
                                <option value="applicant">Абитуриент</option>
                                <option value="operator">Оператор</option>
                                <option value="registrar">Регистратор</option>
                                <option value="administrator">Администратор</option>
                            </select>
                        </div>
                    </Flex>

                    {role === 'operator' && (
                        <div className={styles.formGroup}>
                            <Flex>
                                <div className={styles.formGroup}>
                                    <label>Кабинет:</label>
                                    <input type="text" inputMode='numeric' value={cabinet} onChange={(e) => handleNumericInputChange(e.target.value, setCabinet)} pattern="[0-9]*" />
                                    {formErrors.cabinet && <span className="error-message">{formErrors.cabinet}</span>}
                                </div>
                                <div className={styles.formGroup}>
                                    <label>Окно:</label>
                                    <input type="text"
                                        value={window}
                                        onChange={(e) => handleNumericInputChange(e.target.value, setWindow)}
                                        pattern="[0-9]*"
                                        inputMode="numeric" />
                                    {formErrors.window && <span className="error-message">{formErrors.window}</span>}

                                </div>
                            </Flex>
                            <label>Очереди:</label>
                                <Multiselect
                                    options={cities} // Options to display in the dropdown
                                    onSelect={(e) => setSelectedQueue(e.value)} // Function will trigger on select event
                                    displayValue="name"
                                    placeholder='Выберите очередь'
                                    showCheckbox
                                    className={styles.multiSelectContainer}
                                    style={{ 
                                        chips: { 
                                            background: '#319f42'
                                        },
                                        searchBox: {
                                            border: 'none',
                                            padding: '0'
                                        }
                                    }}
                                />


                        </div>
                    )}
                    <Button submit>Добавить пользователя</Button>
                </form>}

            </Container>

        </Container>
    )
}

export default Admin
